<script lang="ts">
	import qs from 'qs'
	import {
		Timer
	} from '@element-plus/icons'

	export default {
		components: {
			Timer,
		},
		data() {
			return {
				splist: [],
				approveform: '',
				drawer: false,
				innerDrawer: false,
				pageno: 1,
				pagesize: 5,
				total: 0,
				query: {
					name: ''
				},
				tableData: [],
			}
		},
		methods: { 
			rowClick(index, row) {
				try {
					this.$router.push({
						path: row.approve_flow.flowUrl,
						params: {
							table: row.approve_table
						}
					});
				} catch (err) {
					this.$router.replace("/404");
				}
			},
			SelectHistory(val) {
				let prim = {
					id: val.approveId
				};
				let qsprim = qs.stringify(prim);
				this.axios.post("approve/selectid", qsprim).then(res => {
					if (res.code == 1) {
						console.log(res,"低洼低洼的");
						this.splist = res.obj.spList;
						this.splist.forEach(re=>{
							if(re.flowSpTime==null){
								re.flowSpTime="(ง •̀_•́)ง..."
							}
						})
						
					}
				})
			},
			search() {
				this.selectall();
			},
			handleSizeChange(val) {
				this.pagesize = val;
				this.selectall();
			},
			handleCurrentChange(val) {
				this.pageno = val;
				this.selectall();
			},
			handleEdit(index, row) {
				this.drawer = true;
				let prim = {
					id: row.approve_flow.flowId
				};
				let qsprim = qs.stringify(prim);
				this.axios.post("node/selectid", qsprim).then(res => {
					if (res.code == 1) {
						this.approveform = res.data;
						this.SelectHistory(row);
					}
				})


			},
			selectall() {
				let PrimName = {
					no: this.pageno,
					size: this.pagesize,
					name: this.query.name,
					emp:4
				};
				console.log(PrimName);
				this.axios.post("approve/byidsp", PrimName).then(res => {
					console.log("返回",res);
					this.tableData = res.obj.list;;
					this.total = res.obj.total;
					this.pageno = res.obj.pageNum;
					this.pagesize = res.obj.pageSize;
				})
			}
		},
		created() {
			this.selectall();
		}
	}
</script>

